	'use strict';
	
	// Doc ready
	$(document).ready(function() {
	
	  // Initialise validation
	  Validation.init();
	
	  $('[name=terms]').change(function() {
	    Validation.checkbox(this);
	  });
	
	  // Continue button click
	  $('[am-form~=submit]').click(function(e) {
	
	    Validation.section('[am-form~=register]');
	
	    Validation.checkbox('[name=terms]');
	
	    // Scroll form to invalid field
	    $('html, body').animate({
	      scrollTop: $('.invalid').offset().top - 50
	    }, 300);
	
	    // Prevent default action
	    e.preventDefault();
	  });
	
	  // Dropdown navigation
	  $('[am-nav~=button]').click(function() {
	    if ($(window).width() <= 767) {
	      $('[am-nav~=public]').slideToggle();
	    }
	  });
	
	  // Auto complete
	  $('[am-form~=auto-suggest] input').on('keyup', function() {
	
	    var input = $(this),
	      input_text = input.val(),
	      text_length = input_text.length;
	
	    // Show results
	    if (text_length > 2) {
	
	      $('[am-form~=auto-suggest] [am-form~=suggest-list]').slideDown();
	
	    } else {
	
	      $('[am-form~=auto-suggest] [am-form~=suggest-list]').slideUp();
	    }
	
	    // Populate input on click
	    $('[am-form~=suggest-item]').on('click', function(e) {
	      e.preventDefault();
	      var suggestion = $.trim($(this).text());
	      input.val(suggestion);
	      $('[am-form~=auto-suggest] [am-form~=suggest-list]').slideUp();
	    });
	  });
	
	  // Hide dropdown when input blurred
	  $('[am-form~=auto-suggest] input').blur(function() {
	    $('[am-form~=auto-suggest] [am-form~=suggest-list]').slideUp();
	  });
	
	});


